script.code:
var facts = [directFact, kondFact, techColFact, exColFact, tailFact]

console.log("trigger")
try {
	var density = new UaNode("AGENT.OBJECTS.ServiceParams.Mud.currentMud.density")
	var viscosity = new UaNode("AGENT.OBJECTS.ServiceParams.Mud.currentMud.viscosity")
	var waterLoss = new UaNode("AGENT.OBJECTS.ServiceParams.Mud.currentMud.waterLoss")

	function findLast(facts) {
		var last
		var len = facts.reverse().length

		for (var i = 0; i < len; i++) {
			var rows = JSON.parse(facts[i].value).rows
			console.log("rows: ", rows);
			if (rows && rows.length > 0 && facts[i].servertime > startDate) {
				last = rows[rows.length - 1]
				console.log(last)
				break
			}
		}	
		return last || 0
	}
	//console.log(findLast(facts));

	function fillParams(facts) {
		var row = findLast(facts)
		console.log(row)
		if (row) {
		// gomennasorry
			Object.entries(row).map(function(val) {
				console.log(val)
				if (val[0].includes("Плотность")) {
					//console.log("true1")
					density.assign({ value: val[1].replace(",", ".") || "0" })
				}
				if (val[0].includes("Условная вязкость")) {
					//console.log("true2")
					viscosity.assign({ value: val[1].replace(",", ".") || "0" })
				}
				if (val[0].includes("Водоотдача")) {
					 //console.log("true3")
					 waterLoss.assign({ value: val[1].replace(",", ".") || "0" })
				}
			})
		}
	}

	fillParams(facts)
}
catch(err) {
	console.log(err.message || err)
}



